<?php$news_args_array = array(	'public'             => true,	'publicly_queryable' => true,	'exclude_from_search'=> true,	'show_ui'            => true,	'show_in_menu'       => true,	'query_var'          => true,	'capability_type'    => 'post',	'hierarchical'       => false,	'has_archive'        => true,	'rewrite'            => array( 		'slug'       => 'news',		'with_front' => false	),	//'menu_icon'          => plugins_url( 'images/newspaper.png', __FILE__ ),	'supports'           => array(		'title',		'editor',		'thumbnail',		// 'custom-fields',		'page-attributes',	),	'labels'              => array(		'name'              => _x( 'News', 'twentytwelve' ),		'singular_name'     => _x( 'News', 'twentytwelve' ),		'add_new'           => _x( 'Add New', 'twentytwelve' ),		'add_new_item'      => _x( 'Add New News', 'twentytwelve' ),		'edit_item'         => _x( 'Edit News', 'twentytwelve' ),		'new_item'          => _x( 'New News', 'twentytwelve' ),		'view_item'         => _x( 'View News', 'twentytwelve' ),		'search_items'      => _x( 'Search News', 'twentytwelve' ),		'not_found'         => _x( 'No News found', 'twentytwelve' ),		'not_found_in_trash'=> _x( 'No News in Trash', 'twentytwelve' ),		'parent_item_colon' => _x( 'Parent News:', 'twentytwelve' ),		'menu_name'         => _x( 'News', 'twentytwelve' ),	));sd_register_post_type( 'news', $news_args_array, 'news' );// News Categoryfunction post_type_new() {		register_taxonomy('newscat','news',	array(		'label'             => 'News Category',		'rewrite'           => array(			'slug'                => 'news',			'with_front'          => false		),		'hierarchical'      => true,		'single_value'      => true,		'show_admin_column' => true,	));}add_action('init', 'post_type_new');/****************************************************** *   *  Register Columns for Custom Post Type. *   ******************************************************/function project_add_columnaaa($columns) {		$new_columns = array(		'cb'                      => '<input type="checkbox" />',		'project_thumbnail'       => __(''),		'title'                   => __('Title'),		'project_status'        => __('Status'),		'date'                    => __('Date'),		// 'project_order'        => __('Order'),	);		return $new_columns;	}add_filter( 'manage_edit-project_columns' , 'project_add_columnaaa');/****************************************************** *   *  Render data for registered columns. *   ******************************************************/function project_columnsss( $column_name, $post_id ){	global $wpdb, $post;		switch ($column_name) {				case 'project_thumbnail' :			if( has_post_thumbnail( $post_id ) ){				$header_image_size = 'admin_thumb';				$header_image_data = wp_get_attachment_image_src( get_post_thumbnail_id( $post->ID ), $header_image_size );				$header_image_src = $header_image_data[0];				$header_image_width = $header_image_data[1];				$header_image_height = $header_image_data[2];			}else{				// $header_image_src = get_stylesheet_directory_uri() . '/assets/images/no-image/no_image_60x60.png';				// $header_image_width = 60;				// $header_image_height = 60;			}			if( !empty($header_image_src) ){				?>				<img src="<?php echo $header_image_src;?>" alt="<?php the_title(); ?>" width="<?php echo $header_image_width;?>" height="<?php echo $header_image_height;?>" />				<?php			}			break;				default:			break;			} // end switch}add_action( 'manage_project_posts_custom_column' , 'project_columnsss', 10, 2 );?>