<?php$results_args_array = array(	'public'             => true,	'publicly_queryable' => true,	'exclude_from_search'=> true,	'show_ui'            => true,	'show_in_menu'       => true,	'query_var'          => true,	'capability_type'    => 'post',	'hierarchical'       => false,	'has_archive'        => true,	'rewrite'            => array( 		'slug'       => 'results',		'with_front' => false	),	//'menu_icon'          => plugins_url( 'images/newspaper.png', __FILE__ ),	'supports'           => array(		'title',		'editor',		'thumbnail',		// 'custom-fields',		'page-attributes',	),	'labels'              => array(		'name'              => _x( 'Results', 'twentytwelve' ),		'singular_name'     => _x( 'Results', 'twentytwelve' ),		'add_new'           => _x( 'Add New', 'twentytwelve' ),		'add_new_item'      => _x( 'Add New Results', 'twentytwelve' ),		'edit_item'         => _x( 'Edit Results', 'twentytwelve' ),		'new_item'          => _x( 'New Results', 'twentytwelve' ),		'view_item'         => _x( 'View Results', 'twentytwelve' ),		'search_items'      => _x( 'Search Results', 'twentytwelve' ),		'not_found'         => _x( 'No Results found', 'twentytwelve' ),		'not_found_in_trash'=> _x( 'No Results in Trash', 'twentytwelve' ),		'parent_item_colon' => _x( 'Parent Results:', 'twentytwelve' ),		'menu_name'         => _x( 'Results', 'twentytwelve' ),	));sd_register_post_type( 'results', $results_args_array, 'results' );// Results post typefunction post_type_results() {		register_taxonomy('rescat','results', array(		'label'       => 'Category',		'rewrite'     => array(			'slug'       => 'results-category',			'with_front' => false		),		'hierarchical'    => true,		'show_admin_column' => true,		// 'single_value' => true	));	}add_action('init', 'post_type_results');/****************************************************** *   *  Register Columns for Custom Post Type. *   ******************************************************/function results_add_column($columns) {		$new_columns = array(		'cb'                      => '<input type="checkbox" />',		'result_thumbnail'        => __(''),		'title'                   => __('Title'),		'results_rescat'          => __('Category'),		'date'                    => __('Date'),		// 'project_order'        => __('Order'),	);		return $new_columns;	}// add_filter( 'manage_edit-results_columns' , 'results_add_column');/****************************************************** *   *  Render data for registered columns. *   ******************************************************/function results_column( $column_name, $post_id ){	global $wpdb, $post;		switch ($column_name) {				case 'result_thumbnail' :			if( has_post_thumbnail( $post_id ) ){				$header_image_size = 'admin_thumb';				$header_image_data = wp_get_attachment_image_src( get_post_thumbnail_id( $post->ID ), $header_image_size );				$header_image_src = $header_image_data[0];				$header_image_width = $header_image_data[1];				$header_image_height = $header_image_data[2];			}else{				// $header_image_src = get_stylesheet_directory_uri() . '/assets/images/no-image/no_image_60x60.png';				// $header_image_width = 60;				// $header_image_height = 60;			}			if( !empty($header_image_src) ){				?>				<img src="<?php echo $header_image_src;?>" alt="<?php the_title(); ?>" width="<?php echo $header_image_width;?>" height="<?php echo $header_image_height;?>" />				<?php			}			break;				case 'result_rescat' :			$rescat = get_taxonomy( 'rescat' )->labels->name;			echo '<pre>';			print_r($rescat);			var_dump($rescat);			echo '</pre>';				default:			break;			} // end switch}// add_action( 'manage_results_posts_custom_column' , 'results_column', 10, 2 );?>