<?php$pslider_args_array = array(	'supports' => array(		'title',		// 'editor',		'thumbnail',		// 'custom-fields',		// 'page-attributes',	),	'exclude_from_search' => true,	'labels'              => array(		'name'              => _x( 'Sliders', 'twentytwelve' ),		'singular_name'     => _x( 'Slider', 'twentytwelve' ),		'add_new'           => _x( 'Add New', 'twentytwelve' ),		'add_new_item'      => _x( 'Add New Slider', 'twentytwelve' ),		'edit_item'         => _x( 'Edit Slider', 'twentytwelve' ),		'new_item'          => _x( 'New Slider', 'twentytwelve' ),		'view_item'         => _x( 'View Slider', 'twentytwelve' ),		'search_items'      => _x( 'Search Slider', 'twentytwelve' ),		'not_found'         => _x( 'No slider found', 'twentytwelve' ),		'not_found_in_trash'=> _x( 'No slider in Trash', 'twentytwelve' ),		'parent_item_colon' => _x( 'Parent Slider:', 'twentytwelve' ),		'menu_name'         => _x( 'Sliders', 'twentytwelve' ),	));sd_register_post_type( 'pslider', $pslider_args_array, 'psliders' );/****************************************************** *   *  Register Columns for Custom Post Type. *   ******************************************************/function pslider_add_column($columns) {		$new_columns = array(		'cb'                      => '<input type="checkbox" />',		'slider_thumbnail'        => __(''),		'title'                   => __('Title'),		'date'                    => __('Date'),		// 'pslider_order'        => __('Order'),	);		return $new_columns;	}add_filter( 'manage_edit-pslider_columns' , 'pslider_add_column');/****************************************************** *   *  Render data for registered columns. *   ******************************************************/function pslider_column( $column_name, $post_id ){	global $wpdb, $post;		switch ($column_name) {				case 'pslider_order' :						$curr_post_data = get_post( $post_id );			echo $curr_post_data->menu_order;			break;					case 'slider_thumbnail' :			if( has_post_thumbnail( $post_id ) ){				$header_image_size = 'admin_thumb';				$header_image_data = wp_get_attachment_image_src( get_post_thumbnail_id( $post->ID ), $header_image_size );				$header_image_src = $header_image_data[0];				$header_image_width = $header_image_data[1];				$header_image_height = $header_image_data[2];			}else{				// $header_image_src = get_stylesheet_directory_uri() . '/assets/images/no-image/no_image_60x60.png';				// $header_image_width = 60;				// $header_image_height = 60;			}			if( !empty($header_image_src) ){				?>				<img src="<?php echo $header_image_src;?>" alt="<?php the_title(); ?>" width="<?php echo $header_image_width;?>" height="<?php echo $header_image_height;?>" />				<?php			}			break;				default:			break;			} // end switch}add_action( 'manage_pslider_posts_custom_column' , 'pslider_column', 10, 2 );?>