em'          => _x( 'New About Us', 'twentytwelve' ),		// 'view_item'         => _x( 'View About Us', 'twentytwelve' ),		// 'search_items'      => _x( 'Search About Us', 'twentytwelve' ),		// 'not_found'         => _x( 'No about us found', 'twentytwelve' ),		// 'not_found_in_trash'=> _x( 'No about us found in Trash', 'twentytwelve' ),		// 'parent_item_colon' => _x( 'Parent About Us:', 'twentytwelve' ),		// 'menu_name'         => _x( 'About Us', 'twentytwelve' ),	// ));// sd_register_post_type( 'location', $location_args_array, 'locations' );*//****************************************************** *   *  Register Columns for Custom Post Type. *   ******************************************************/function location_add_column($columns) {		$new_columns = array(		'cb'                      => '<input type="checkbox" />',		'location_thumbnail'       => __(''),		'title'                   => __('Title'),		// 'location_status'        => __('Status'),		'date'                    => __('Date'),		// 'location_order'        => __('Order'),	);		return $new_columns;	}add_filter( 'manage_edit-location_columns' , 'location_add_column');/****************************************************** *   *  Render data for registered columns. *   ******************************************************/function location_column( $column_name, $post_id ){	global $wpdb, $post;		switch ($column_name) {				case 'location_order' :			$curr_post_data = get_post( $post_id );			echo $curr_post_data->menu_order;			break;				case 'location_status' :			$location_status = get_field( 'location_status', $post_id );			$location_stat = array(				'upcoming'     => 'Upcoming Location',				'completed'    => 'Completed',				'construction' => 'Under Construction',			);			if( !empty($location_status) ){				echo $location_stat[$location_status];			}			/*			if( $location_status == 1 ){				echo '<i class="icon-check icon-2"></i>';			}			*/			break;				case 'location_thumbnail' :			if( has_post_thumbnail( $post_id ) ){				$header_image_size = 'admin_thumb';				$header_image_data = wp_get_attachment_image_src( get_post_thumbnail_id( $post->ID ), $header_image_size );				$header_image_src = $header_image_data[0];				$header_image_width = $header_image_data[1];				$header_image_height = $header_image_data[2];			}else{				// $header_image_src = get_stylesheet_directory_uri() . '/assets/images/no-image/no_image_60x60.png';				// $header_image_width = 60;				// $header_image_height = 60;			}			if( !empty($header_image_src) ){				?>				<img src="<?php echo $header_image_src;?>" alt="<?php the_title(); ?>" width="<?php echo $header_image_width;?>" height="<?php echo $header_image_height;?>" />				<?php			}			break;				default:			break;			} // end switch}add_action( 'manage_location_posts_custom_column' , 'location_column', 10, 2 );?>